require('sprites')
require('parallax')
require('level')
require('santa')
require('elf')
require('item')
require('timer')
require('music')
require('draw')
require('credits')
require('global')
require('chmode')

t=0

maxtimer=36
timer=maxtimer

function ssanta()
  if lvl<5 then music2()
  else bossmusic() end

  santa_input()
  advance_timer()
  
  render_background()
  render_foreground()

  --if not elf_cor then
  t=t+1
  --end
end

TIC=ssanta
cls(0)

-- after game over, restart from the current level
if pmem(99)>0 then
  lvl=pmem(99)
  pmem(99,0)
end
generate()

-- has the main game been completed?
if pmem(255)>0 then
  TIC=challenge
else
  TIC=modal
end
